#include<iostream>using namespace std;int const NameLen = 20;void  order(char name[][NameLen], int n)	//字符串排序{    char temp[NameLen];    for (int i = 0; i < n - 1; i++)	//冒泡排序        for (int j = i + 1; j < n; j++)            if (strcmp(name[i], name[j]) > 0)	//比较两个字符串的大小            {                strcpy(temp, name[i]);	//字符串交换                strcpy(name[i], name[j]);                strcpy(name[j], temp);            }}int find(char name[][NameLen], int n, char searchname[NameLen]){	for (int i = 0; i < n; i++)		if (strcmp(name[i], searchname) == 0)             //找到,返回位置			return i + 1;		else if (strcmp(name[i], searchname) > 0)        //未找完,但找不到,返回0			return 0;	return 0;                                                                //找完,找不到,返回0}int main(){	char NameTab[][NameLen] = { "GongJing","LiuNa","HuangPin","ZhouZijun",	"LianXiaolei","ChenHailing","CuiPeng","LiuPing" };	char searchname[NameLen];	int n = sizeof(NameTab) / NameLen;	order(NameTab, n);	for (int i = 0; i < n; i++) //输出排序后的各姓名		cout << i + 1 << '\t' << NameTab[i] << endl;	cout << "Input the searching name:";	cin >> searchname;	if (n = find(NameTab, n, searchname))		cout << "Position:" << n << endl;	else		cout << "Not found!" << endl;	return 0;}